INSERT INTO `filmprogram` (`id`, `title`, `text`) VALUES
(1, 'الجلسة الافتتاحية', '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: right;\">سيتم فيها التعريف عن طبيعة اليوم وأهدافه وتقديم البرنامج الخاص بالفعالية، بالإضافة إلى تقديم المتحدثين، ستستغرق فقرة الافتتاحية مدة&nbsp;<strong>15</strong>&nbsp;دقيقة تبدأ الساعة<strong>10:00</strong>&nbsp;صباحاً وتنتهي الساعة&nbsp;<strong>10:15</strong>.</p>'),
(2, 'الجلسات الاختصاصية', '<p style=\"text-align: right;\">تنطلق الجلسات الاختصاصية الخمس الساعة&nbsp;<strong>10:15</strong>&nbsp;صباحاً وتنتهي على الساعة&nbsp;<strong>15:00</strong>&nbsp;عصراً، حيث ستكون كل جلسة&nbsp;<strong>40</strong>&nbsp;دقيقة نصفها للنقاش مع الجمهور:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align: right;\">عرض مجموعة من الأفلام المختارة.</li>\r\n	<li style=\"text-align: right;\">مداخلة من قبل مختص في مجال من المجالات المحددة.</li>\r\n	<li style=\"text-align: right;\">النقاش مع الحضور والإجابة عن الأسئلة.</li>\r\n</ul>'),
(3, 'الجلسة الختامية', '<p style=\"text-align: right;\">سيتم اختيار فيلماً مميزاً للنقاش العام من قبل الضيوف من زاويا مختلفة، وتنطلق الجلسة الختامية الساعة&nbsp;<strong>15:10</strong>&nbsp;ظهراً وتنتهي على الساعة&nbsp;<strong>15:50</strong>&nbsp;عصراً، يليها خطاب ختامي تلخيصي لأهم ما ورد في اليوم.</p>');

